mapper.select:
select ga.id, ga.create_user_id , ga.user_num , ga.box_num , ga.total_bean, ga.status, ga.win_user_id , ga.draw_code , ga.created_at , ga.updated_at , u.name as userName, u.avatar as avatar, ab.id as boxId, bx.name as boxName, bx.intact_cover as intactCover, guu.id as gameUserId, guu.name as gameUserName, guu.avatar as gameAvatar, guu.bean as gameBean from game_award_records gar inner join game_arenas ga on ga.id = gar.game_arena_id and ga.status=2 left join users u on u.id = ga.create_user_id left join game_arena_box ab on ab.game_arena_id = ga.id inner join boxs bx on ab.box_id = bx.id left join game_arena_users gu on gu.game_arena_id = ga.id inner join users guu on guu.id = gu.user_id
and gar.user_id =#{userId}
order by ga.status ,ga.created_at desc
