mapper.select:
select re.id, re.user_id as userId, re.use_bean as boxBean, u.name as userName, re.percent as percent, re.award_id as awardId, re.award_name as targetName, re.award_dura as awardDura , re.award_lv as awardLv , re.bean as targetValue, re.get_award_id as getAwardId, aw.cover as targetCover, aw2.cover as obtainCover, re.get_award_name as obtainName, re.get_award_dura as getAwardDura, re.get_award_lv as getAwardLv, re.get_bean as obtainValue, re.created_at as createdAt, re.updated_at as updatedAt from lucky_box_records re left join users u on re.user_id = u.id left join box_awards aw on aw.id = re.award_id left join box_awards aw2 on aw2.id = re.get_award_id
and u.name =#{userName}
and re.award_name =#{awardName}
order by re.created_at desc

select re.id, re.user_id as userId, re.use_bean as boxBean, u.name as userName, u.avatar as avatar, re.percent as percent, re.award_id as awardId, re.award_name as targetName, re.award_dura as awardDura , re.award_lv as awardLv , re.bean as targetValue, re.get_award_id as getAwardId, aw.cover as targetCover, aw2.cover as obtainCover, re.get_award_name as obtainName, re.get_award_dura as getAwardDura, re.get_award_lv as getAwardLv, re.get_bean as obtainValue, re.created_at as createdAt, re.updated_at as updatedAt from lucky_box_records re left join users u on re.user_id = u.id left join box_awards aw on aw.id = re.award_id left join box_awards aw2 on aw2.id = re.get_award_id
re.award_id = #{awardId}
order by re.created_at desc limit 20
